<?phpclass tx_pagebrowse_tt_news_userPageBrowserFunc {	public function userPageBrowserFunc ($markerArray, $params) {			// make a reference to the parent object		$this->pObj = &$params['parentObj']; 		$res_count = $this->pObj->internal['res_count'];		$results_at_a_time = $this->pObj->internal['results_at_a_time'];		$numberOfPages = intval($res_count/$results_at_a_time) + (($res_count % $results_at_a_time) == 0 ? 0 : 1);				$markerArray['###BROWSE_LINKS###'] = $this->getListGetPageBrowser($numberOfPages);			return $markerArray;	}	protected function getListGetPageBrowser($numberOfPages) {		// Get default configuration		$conf = $GLOBALS['TSFE']->tmpl->setup['plugin.']['tx_pagebrowse_pi1.'];		// Modify this configuration		$conf += array(			'pageParameterName' => $this->pObj->prefixId . '|pointer',			'numberOfPages' => $numberOfPages,		);		// Get page browser		$cObj = t3lib_div::makeInstance('tslib_cObj');		/* @var $cObj tslib_cObj */		$cObj->start(array(), '');				return $cObj->cObjGetSingle('USER', $conf);	}	}if (defined('TYPO3_MODE') && $TYPO3_CONF_VARS[TYPO3_MODE]['XCLASS']['ext/pagebrowse_tt_news/class.tx_pagebrowse_tt_news_userPageBrowserFunc.php']) {	include_once($TYPO3_CONF_VARS[TYPO3_MODE]['XCLASS']['ext/pagebrowse_tt_news/class.tx_pagebrowse_tt_news_userPageBrowserFunc.php']);}?>